scripts.postinstall:
if [ ! -f ./firestore.rules ]; then echo 'rules_version = '2';' > ./firestore.rules; else sed -i '1s/^/rules_version = '2';
/' ./firestore.rules; fi && awk '/// #EDGE FIREBASE RULES START/,/// #EDGE FIREBASE RULES END/' ./src/firestore.rules | sed 's//\/g; s/&/\&/g' | sed -e 's/"/\"/g' -e 's///\//g' | sed -e '1s/^/// #EDGE FIREBASE RULES START
/' -e '$s/$/
// #EDGE FIREBASE RULES END/' >> ./firestore.rules
